import React, { useState, useEffect, useRef } from "react";
const { tableau } = window;

function Filter() {
  const url = 'https://public.tableau.com/views/HousingwrtSuburbs/Dashboard2';
  const ref = useRef(null);

  const initViz = () => {
    new tableau.Viz(ref.current, url, {
      width: "100%",
      height: "100%"
    });
  };

  useEffect(initViz, []);

  return (
    <div>
      <div style={setVizStyle} ref={ref} />
    </div>
  );
}

const setVizStyle = {
  width: "100vw",
  height: "100vh",
};
